#include "CTorwart.h"#include <iostream>CTorwart :: CTorwart(std::string name, std::string vorname, unsigned int alter, e_position position, unsigned int nummer){	this->name = name;	this->vorname = vorname;	this->alter = alter;	this->position = position;	this->nummer = nummer;}void CTorwart::setStrafRaumBeherrschung(unsigned int argStrafRaumBeherrschung){	this->strafRaumBeherrschung = argStrafRaumBeherrschung;}void CTorwart::setLinienQualitaet(unsigned int argLinienQualitaet){	this->linienQualitaet = argLinienQualitaet;}void CTorwart::setAbwehrKommunikation(unsigned int argAbwehrKommunikation){	this->abwehrKommunikation = argAbwehrKommunikation;}void CTorwart::print() const{	std::cout << "Goalkeeper " << name << " " << vorname		<< " is " << alter << "years old. His position is "		<< position << ", his number is " << nummer << ". "		<< "Penalty room restraint: " << strafRaumBeherrschung << " "		<< "Lines quality: " << linienQualitaet << " "		<< "Defense communication: " << abwehrKommunikation << "."		<< std::endl;}