#include "CTrainer.h"#include <iostream>CTrainer::CTrainer(std::string name, std::string vorname, unsigned int alter){    this->name = name;    this->vorname = vorname;    this->alter = alter;}void CTrainer::setLizenzStufe(unsigned int argLizenzStufe){    this->lizenzStufe = argLizenzStufe;}void CTrainer::print() const{    std::cout << name << " " << vorname << " "         << "is a " << alter << " years old trainer and his licence level is "         << lizenzStufe << "."         << std::endl;}